with open("datafiles/pubmedsearchresults.txt", "r") as f: filtered_counts = ast.literal_eval(f.read())
# log to account for big differences log_freq = {key: round(10*math.log(value)) for key, value in filtered_counts.items()}
#wc = WordCloud(background_color= '#FFFFFF', contour_color= '#FFFFFF', mask = brain_mask, width = 800, height = 500, random_state=123).generate_from_frequencies(filtered_counts) wc = WordCloud(colormap = 'brg_r', prefer_horizontal=0.7, background_color= '#FFFFFF', contour_color= '#FFFFFF', mask = brain_mask, width = 800, height = 500, random_state=3).generate_from_frequencies(log_freq) plt.axis('off') plt.imshow(wc, interpolation='bilinear') plt.savefig('../images/wordcloud_pubmed.png', bbox_inches='tight', dpi=300) #plt.show()
